import React, { Component } from 'react'
import Matter from 'matter-js';


class App extends Component {
  constructor (props) {
    super(props)
    this.state = {}
  }


  static async getInitialProps({req, res}) {

     return {};
  }


  componentDidMount() {
    this.d();
    console.log('GrandChild did mount.');
    setTimeout(() => {
      Matter.Body.setMass(this.frosch, 1)
      Matter.Body.applyForce(this.frosch, this.frosch.position, { x: 0, y: -0.07 });
    }, 1000);
  }

  d(){
    var Engine = Matter.Engine,
        Render = Matter.Render,
        World = Matter.World,
        Events = Matter.Events,
        Bodies = Matter.Bodies;

    var engine = Engine.create();

    var render = Render.create({
        element: document.body,
        engine: engine
    });

    this.highestPoint = 0;


    this.frosch = Bodies.rectangle(400, 600, 20, 20);
    this.frosch.restitution = 0.8;

    this.end = Bodies.rectangle(400, 100, 100, 20, { isStatic: true});


    var ground = Bodies.rectangle(400, 610, 810, 60, { isStatic: true });

    World.add(engine.world, [this.frosch, this.end, ground]);

        // run the engine
    Engine.run(engine);

    // run the renderer
    Render.run(render);

    Events.on(engine, 'beforeUpdate', (event) => {
        var engine = event.source;

        this.highestPoint = this.frosch.position.y;


        console.log(this.frosch.position);
    });
    Events.on(engine, 'collisionStart', (event) => {
      console.log("coll");
      var pairs = event.pairs;

      // change object colours to show those in an active collision (e.g. resting contact)
      for (var i = 0; i < pairs.length; i++) {
          var pair = pairs[i];
          pair.bodyA.render.fillStyle = '#333';
          pair.bodyB.render.fillStyle = '#333';
      }
    });

  }

  render () {
    return (
      <div>
        Hau den Lukas
      </div>
    )
  }
}

export default App
